import React,{Component} from'react';

class ToDoList extends Component{

  constructor(){
      super();
      this.state={
         userInput:"",
         items:[]
      }
  }


onChange(event){

   this.setState({
       userInput:event.target.value
   },()=>console.log(this.state.userInput))

}



 addTodo(event){
    event.preventDefault()
       this.setState({

         userInput:'',

        items:[...this.state.items,this.state.userInput]   
       },()=>console.log(this.state))
 }


   deleteTodo(event){
       event.preventDefault();

      const array= this.state.items;
      const index = array.indexOf(event.target.value);
      array.splice(index, 1);
       this.setState({
           items:array
       })

    }



  renderTOdo(){
      return this.state.items.map((item)=>{
          return(
              <div key= {item}>
                  {item}|<button onClick={this.deleteTodo.bind(this)}>*</button>
                  </div>
          ) 

      })
  }

    render(){
        return(
            <div>
                <h1 align="center">it's the last ligne of my new project</h1>

                <from className="form-row align-items-center">

                     <input 

                      value={this.state.userInput} 
                        type="text" 
                          placeholder="rensigne moi"
                          onChange={this.onChange.bind(this)}
                          className="form-control mb-2"
                          />

                    <button 
                     className=""
                    onClick={this.addTodo.bind(this)} >ajouter</button>

                </from>

                <div>
                    {this.renderTOdo()}
                </div>

                </div>
        )
    }
}

export default ToDoList;
